import { useState } from "react";
import "./styles.css";
import { Magic } from "magic-sdk";
import { ConnectExtension } from "@magic-ext/connect";
import Web3 from "web3";
import Web3Modal from 'web3modal'
//import { create as ipfsHttpClient } from 'ipfs-http-client'
import { useRouter } from 'next/router'

const magic = new Magic("pk_live_73AAE8A5F81B1CF3", {
  network: "rinkeby",
  locale: "en_US",
  extensions: [new ConnectExtension()]
});
const web3 = new Web3(magic.rpcProvider);

export default function App() {
  const [account, setAccount] = useState(null);
  const [fileUrl, setFileUrl] = useState(null)
  const [regCount, setRegCount] = useState(0)
  const [formInput, updateFormInput] = useState({ gmail: '', name: '', organisation: '' , designation: '' })
  const router = useRouter()





   async function uploadToIPFS() {
    const { gmail, name, organisation, designation } = formInput
    if (!gmail || !name || !organisation || !designation ) {
      return
    } else {
      // first, upload metadata to IPFS
      const data = JSON.stringify({
        gmail, name, organisation, designation
      }) 
      try{
                    // const resFile = await axios({
                    //     method: "post",
                    //     url: "https://api.pinata.cloud/pinning/pinFileToIPFS",
                    //     data: data,
                    //     headers:{
                    //         'pinata_api_key':PINATA_KEY,
                    //         'pinata_secret_api_key':PINATA_SECRET,
                    //         'Content-Type':'multipart/form-data'
                    //     },
                    // });
                    //const url = `https://gateway.pinata.cloud/ipfs/${resFile.data.IpfsHash}`;
                    //alert("url onChange "+ url);
                    //const ImageURL = `https://gateway.pinata.cloud/ipfs/${resFile.data.IpfsHash}`;
                    setFileUrl("https://gateway.pinata.cloud/ipfs/QmWyUTERPpKzY7tzA2iDzfzktNG8PSjj9PXZYU4Hx2jFB4");
                    return fileUrl;
            } //end of try
        catch (error) {
        console.log('Error uploading file: ', error)
        alert("Failure in uploadToIPFS -" + error)
      } 
    }
  }

  async function eventRegstration() {
    const web3Modal = new Web3Modal()
    //console.log("line 99  : " + web3Modal);
    const provider = await web3Modal.connect()
    const web3 = new Web3(provider)
    //const url = await uploadToIPFS()
    const networkId = await web3.eth.net.getId()
    // console.log("line 104  : " + networkId);
    const { gmail, name, organisation, designation } = formInput
    // console.log("line 106  : " + gmail + " " + name);
    // Mint the NFT
    const HexEventRegistrationContractAddress = HexEventRegistration.networks[networkId].address;
    // console.log("line 109  : " + HexEventRegistrationContractAddress);
    const HexEventRegistrationContract = new web3.eth.Contract(HexEventRegistration.abi, HexEventRegistrationContractAddress)
    //const HexEventRegistrationContract = new web3.eth.Contract(HexEventRegistration.abi, '0x80731b8989B7A2339054EaBF2Ffe50024A8b5497');
    // console.log("line 111  : " + HexEventRegistrationContract);
    const accounts = await web3.eth.getAccounts()
    return HexEventRegistrationContract;
    // console.log("line 113  : " + accounts[0]);
    //const HexEventRegistrationContract = new web3.eth.Contract(HexEventRegistration.abi, HexEventRegistration.networks[networkId].address)
    //let listingFee = await HexEventRegistrationContract.methods.getListingFee().call()
    //listingFee = listingFee.toString()
    //alert("url line 69 - " + url)
    //alert("account used for listing " + accounts[0])
    // HexEventRegistration.methods.mint(url).send({ from: accounts[0] }).on('receipt', function (receipt) {
    //     console.log('minted');
    //     //List the NFT
    //     const tokenId = receipt.events.NFTMinted.returnValues[0];
    //     HexEventRegistrationContract.methods.createHexRegToken(tokenId, gmail,name,organisation, designation)
    //     .send({ from: accounts[0]}).on('receipt', function () {
    //             console.log('listed');
    //             router.push('/');
    //         });
    // });
      // HexEventRegistrationContract.methods.createHexRegTokenWithAccount(url, gmail,name,organisation, designation)
      // .send({ from: accounts[0]}).on('receipt', function () {
      //     console.log('listed');
      //     router.push('/');
      // });
      // HexEventRegistrationContract.methods.createHexRegTokenWithoutAccount(url, gmail,name,organisation, designation).send({ from: "0xca891C5AFAb66BB9Fb9AFA1840443477Bc81bd88"}).on('receipt', function () {
      //     console.log('listed');
      //     router.push('/');
      // });


    // HexEventRegistrationContract.methods.getParticipentsCount().call().then(function(e){
    //   setRegCount(e)
    // })
    // setTimeout(() => {
    //   HexEventRegistrationContract.methods.getParticipentsCount().call().then(function(e){
    //     setRegCount(e);
    //     // window.location.reload(1);
    //   })
    // }, "3000")


  }

  async function eventRegstrationWA(){
    const web3Modal = new Web3Modal()
    const provider = await web3Modal.connect()
    const web3 = new Web3(provider)
    const networkId = await web3.eth.net.getId()
    const HexEventRegistrationContractAddress = HexEventRegistration.networks[networkId].address;
    const HexEventRegistrationContract = new web3.eth.Contract(HexEventRegistration.abi, HexEventRegistrationContractAddress)
    const accounts = await web3.eth.getAccounts()
    const { gmail, name, organisation, designation } = formInput
    const url = await uploadToIPFS()
    HexEventRegistrationContract.methods.createHexRegTokenWithAccount(url, gmail,name,organisation, designation)
    .send({ from: accounts[0]}).on('receipt', function () {
        console.log('listed');
        router.push('/');
    });
  }
  async function eventRegstrationWNA(){
    const web3Modal = new Web3Modal()
    const provider = await web3Modal.connect()
    const web3 = new Web3(provider)
    const networkId = await web3.eth.net.getId()
    const HexEventRegistrationContractAddress = HexEventRegistration.networks[networkId].address;
    const HexEventRegistrationContract = new web3.eth.Contract(HexEventRegistration.abi, HexEventRegistrationContractAddress)
    //const accounts = await web3.eth.getAccounts()
    const { gmail, name, organisation, designation } = formInput
    const url = await uploadToIPFS()
    HexEventRegistrationContract.methods.createHexRegTokenWithoutAccount(url, gmail,name,organisation, designation)
    .send({ from: 0xca891C5AFAb66BB9Fb9AFA1840443477Bc81bd88}).on('receipt', function () {
        console.log('listed');
        router.push('/');
    });
  }

  const sendTransaction = async () => {
    const publicAddress = (await web3.eth.getAccounts())[0];
    console.log("address" + publicAddress);
    const txnParams = {
      from: publicAddress,
      to: publicAddress,
      value: web3.utils.toWei("0.01", "ether"),
      gasPrice: web3.utils.toWei("30", "gwei")
    };
    web3.eth
      .sendTransaction(txnParams)
      .on("transactionHash", (hash) => {
        console.log("the txn hash that was returned to the sdk:", hash);
      })
      .then((receipt) => {
        console.log("the txn receipt that was returned to the sdk:", receipt);
      })
      .catch((error) => {
        console.log(error);
      });
  };

  const login = async () => {
    web3.eth
      .getAccounts()
      .then((accounts) => {
        setAccount(accounts?.[0]);
      })
      .catch((error) => {
        console.log(error);
      });
  };

  const signMessage = async () => {
    const publicAddress = (await web3.eth.getAccounts())[0];
    const signedMessage = await web3.eth.personal
      .sign("My Message", publicAddress, "")
      .catch((e) => console.log(e));
    console.log(signedMessage);
  };

  const showWallet = () => {
    magic.connect.showWallet().catch((e) => {
      console.log(e);
    });
  };

  const createNft = () => {
    magic.connect.showWallet().catch((e) => {
      console.log(e);
    });
  };

  const disconnect = async () => {
    await magic.connect.disconnect().catch((e) => {
      console.log(e);
    });
    setAccount(null);
  };

  return (
    <div className="app">
      <h2>Magic Connect</h2>
      {!account && (
        <button onClick={login} className="button-row">
          Sign In
        </button>
      )}

      {account && (
        <>
          {/* <button onClick={showWallet} className="button-row">
            Show Wallet
          </button>
          <button onClick={sendTransaction} className="button-row">
            Send Transaction
          </button>
          <button onClick={signMessage} className="button-row">
            Sign Message
          </button>
          <button onClick={createNft} className="button-row">
            Create Nft
          </button>
          <button onClick={disconnect} className="button-row">
            Disconnect
          </button> */}
          <div className="flex justify-center">

      <div className="w-1/2 flex flex-col pb-12">
        {/* <button onClick={eventRegstrationCount} className="font-bold mt-4 bg-teal-400 text-white rounded p-4 shadow-lg">
          No Of Resistrations
        </button> */}
        <div className="top-10 right-10 absolute font-bold text-1xl">
              <h1>Registrations = <button  className="mr-4 text-teal-400 font-bold text-4xl ">{regCount}</button></h1>
        </div>
        {
          fileUrl && (
            <img className="rounded mt-4" width="350" src={fileUrl} />
          )
        }
        <input 
          placeholder="Enter Gmail"
          className="mt-2 border rounded p-4"
          onChange={e => updateFormInput({ ...formInput, gmail: e.target.value })}
        />
        <textarea
          placeholder="Enter Name"
          className="mt-2 border rounded p-4"
          onChange={e => updateFormInput({ ...formInput, name: e.target.value })}
        />
        <input
          placeholder="Organisation"
          className="mt-2 border rounded p-4"
          onChange={e => updateFormInput({ ...formInput, organisation: e.target.value })}
        />
        <input
          placeholder="Designaation"
          className="mt-2 border rounded p-4"
          onChange={e => updateFormInput({ ...formInput, designation: e.target.value })}
        />
        <button onClick={eventRegstrationWNA} className="font-bold mt-4 bg-teal-400 text-white rounded p-4 shadow-lg">
          Registor Without Metamask
        </button>
      </div>
    </div>
        </>

      )}
    </div>
  );
}
